DEFINE FUNCTION OVERWRITE fn::create_wallet($initial_balance: option<float>) {
LET $address = rand::string(10).lowercase();
LET $password = rand::string(16);
LET $hash = crypto::argon2::generate($password);
LET $wallet = (CREATE wallet CONTENT { address: $address, balance: $initial_balance OR 0f, hash: $hash });
RETURN { address: $address, password: $password, wallet: $wallet.first() };
} PERMISSIONS FULL;

DEFINE FUNCTION OVERWRITE fn::transfer_balance($from: record<wallet>, $to: record<wallet> | record<name>, $amount: float) {
UPDATE $from SET balance -= $amount;
UPDATE $from SET total_out += $amount;
RETURN IF record::tb($to) == 'name' {
LET $owner = $to.owner;
UPDATE $owner SET balance += $amount;
UPDATE $owner SET total_in += $amount;
} ELSE {
UPDATE $to SET balance += $amount;
UPDATE $to SET total_in += $amount;
};
} PERMISSIONS FULL;
OPTION IMPORT;

DEFINE TABLE OVERWRITE name TYPE NORMAL SCHEMALESS PERMISSIONS NONE;

DEFINE FIELD OVERWRITE last_transfered ON name TYPE option<datetime> PERMISSIONS FULL;
DEFINE FIELD OVERWRITE last_updated ON name TYPE datetime DEFAULT time::now() PERMISSIONS FULL;
DEFINE FIELD OVERWRITE name ON name TYPE string PERMISSIONS FULL;
DEFINE FIELD OVERWRITE original_owner ON name TYPE option<record<wallet>> PERMISSIONS FULL;
DEFINE FIELD OVERWRITE owner ON name TYPE record<wallet> PERMISSIONS FULL;
DEFINE FIELD OVERWRITE registered ON name TYPE datetime DEFAULT time::now() PERMISSIONS FULL;
DEFINE TABLE OVERWRITE player TYPE ANY SCHEMALESS PERMISSIONS NONE;

DEFINE FIELD OVERWRITE joined_at ON player TYPE datetime DEFAULT time::now() PERMISSIONS FULL;
DEFINE FIELD OVERWRITE name ON player TYPE string PERMISSIONS FULL;
DEFINE TABLE OVERWRITE script_migration SCHEMAFULL
    PERMISSIONS
        FOR select FULL
        FOR create, update, delete NONE;

DEFINE FIELD OVERWRITE script_name ON script_migration TYPE string;
DEFINE FIELD OVERWRITE executed_at ON script_migration TYPE datetime VALUE time::now() READONLY;
OPTION IMPORT;

DEFINE TABLE OVERWRITE transaction TYPE NORMAL SCHEMAFULL PERMISSIONS NONE;

DEFINE FIELD OVERWRITE amount ON transaction TYPE float PERMISSIONS FULL;
DEFINE FIELD OVERWRITE from ON transaction TYPE record<wallet> PERMISSIONS FULL;
DEFINE FIELD OVERWRITE metadata ON transaction TYPE option<string> PERMISSIONS FULL;
DEFINE FIELD OVERWRITE timestamp ON transaction TYPE datetime DEFAULT time::now() PERMISSIONS FULL;
DEFINE FIELD OVERWRITE to ON transaction TYPE record<wallet> | record<name> PERMISSIONS FULL;
DEFINE FIELD OVERWRITE transaction_type ON transaction TYPE 'unknown' | 'mined' | 'name_purchase' | 'transfer' PERMISSIONS FULL;


DEFINE EVENT OVERWRITE transfer_balance ON transaction WHEN $event = 'CREATE' THEN {
LET $from = $after.from;
LET $to = $after.to;
LET $amount = $after.amount;
RETURN fn::transfer_balance($from, $to, $amount);
};

DEFINE TABLE OVERWRITE wallet TYPE NORMAL SCHEMAFULL PERMISSIONS NONE;

DEFINE FIELD OVERWRITE address ON wallet TYPE string PERMISSIONS FULL;
DEFINE FIELD OVERWRITE balance ON wallet TYPE float DEFAULT 0 PERMISSIONS FULL;
DEFINE FIELD OVERWRITE created_at ON wallet TYPE datetime DEFAULT time::now() PERMISSIONS FULL;
DEFINE FIELD OVERWRITE hash ON wallet TYPE string PERMISSIONS FULL;
DEFINE FIELD OVERWRITE is_shared ON wallet TYPE bool DEFAULT false PERMISSIONS FULL;
DEFINE FIELD OVERWRITE total_in ON wallet TYPE float DEFAULT 0 PERMISSIONS FULL;
DEFINE FIELD OVERWRITE total_out ON wallet TYPE float DEFAULT 0 PERMISSIONS FULL;

DEFINE TABLE OVERWRITE owns TYPE RELATION IN player OUT wallet SCHEMAFULL PERMISSIONS NONE;

